import { useContext, useEffect, useState } from 'react';
import './update.css';
import axios from 'axios';
import { Context } from '../../context/Context';
import { useLocation } from 'react-router';

export default function Update() {
  const location = useLocation();
  const path = location.pathname.split('/')[2];
  const [post, setPost] = useState({});
  const [file, setFile] = useState(null);
  const { user } = useContext(Context);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [photo, setPhoto] = useState('');
  const PF = 'http://localhost:5000/images/';

  /*function setImage(e) {
    var name = e.name;
    setFile(name);
    setPhoto(name);
    console.log(name);
  }*/

  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get('/posts/' + path);
      setPost(res.data);
      setTitle(res.data.title);
      setContent(res.data.content);
      setPhoto(res.data.photo);
    };
    getPost();
  }, [path]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const updatedPost = {
      username: user.username,
      title,
      content,
    };
    if (file) {
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append('name', filename);
      data.append('file', file);
      updatedPost.photo = filename;
      try {
        await axios.post('/upload', data);
      } catch (err) {
        console.log(err);
      }
    }
    try {
      await axios.put("/posts/" + post._id, updatedPost);
    } catch (err) {
      console.log(err);
    }
     window.location.assign('/post/' + path);
  };

  return (
    <div className='update'>
      {photo && <img src={PF + photo} alt='' className='updateImg' />}
      <form className='updateForm' onSubmit={handleUpdate}>
        <div className='updateFormGroup'>
          <label htmlFor='fileInput'>
            <i className='updateIcon fas fa-plus'></i>
          </label>
          <input
            type='file'
            id='fileInput'
            style={{ display: 'none' }}
            onChange={(e) => setFile(e.target.files[0])}
          />
          <input
            type='text'
            value={title}
            className='updateInput'
            autoFocus={true}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div className='updateFormGroup'>
          <textarea
            value={content}
            type='text'
            className='updateInput updateText'
            onChange={(e) => setContent(e.target.value)}
          ></textarea>
        </div>
        <button className='updateSubmit' type='submit'>
          Update
        </button>
      </form>
    </div>
  );
}
